import React, { Component } from 'react';
import VideoPlayer from './VideoPlayer';
import video from '../../../assets/Video/video.mp4';
import poster from '../../../assets/Images/video-list-0.jpg'
import MainVideoInfo from './MainVideoInfo';
import MainVideoComments from './main-video-comments/MainVideoComments';

class MainVideo extends Component {

    state = {
        mainVid: {
            id: '1',
            title: 'BMX Rampage: 2018 Highlights',
            description: 'On a gusty day in Southern Utah, a group of 25 daring mountain bikers blew the doors off what is possible on two wheels, unleashing some of the biggest moments the sport has ever seen. While mother nature only allowed for one full run before the conditions made it impossible to ride, that was all that was needed for event veteran Kyle Strait, who won the event for the second time -- eight years after his first Red Cow Rampage title',
            channel: 'Red Cow',
            image: 'type of <string>',
            views: '1,001,023',
            likes: '110,985',
            duration: 'type of <string>',
            video: 'type of <string>',
            timestamp: 'type of <number>',
            comments: [
                {
                    name: 'Micheal Lyons',
                    date: '12/18/2018',
                    comment: 'They BLEW the ROOF off at their last show, once everyone started figuring out they were going. This is still simply the greatest opening of a concert I have EVER witnessed.'
                },
                {
                    name: 'Micheal Lyons',
                    date: '12/18/2018',
                    comment: 'They BLEW the ROOF off at their last show, once everyone started figuring out they were going. This is still simply the greatest opening of a concert I have EVER witnessed.'
                },
                {
                    name: 'Micheal Lyons',
                    date: '12/18/2018',
                    comment: 'They BLEW the ROOF off at their last show, once everyone started figuring out they were going. This is still simply the greatest opening of a concert I have EVER witnessed.'
                }
            ] 
        }
    }

    render () {
        return (
            <>
                <VideoPlayer video = {video} poster = {poster}/>
                <MainVideoInfo title = {this.state.mainVid.title} />
                <MainVideoComments/>
            </>
        );
    }
}
export default MainVideo;
