import React, { Component } from 'react';

class SideVids extends Component {

    renderSideVids = () => {
        return this.props.sideVids.map((item, index) => {
            return (
                <div key = {index}>
                    <div className="side-vid__poster">{this.props.image}</div>
                </div>
            )
        })
    }

    render () {
        console.log(this.props.sideVids);
        return (
            <>
                <div className="divider-line"></div>
                <p className="label">Next video</p>
            </>
        );
    }
}
export default SideVids;
