import React, { Component } from 'react';
import '../../../../styles/comment-form.scss';

class CommentForm extends Component {

    render () {
        return (
            <div className="padding-sides">
                <h2 className="comments-count h2-subheader">Comment count goes here</h2>
                <div className="comment-form">
                    <div className="comment-form__avatar-container">
                        <div className="user-avatar"></div>
                    </div>
                    <div className="comment-form__inputs">
                        <h5 className="label">Join the Conversation</h5>
                        <textarea className="comment-form__inputs--input-box" name="commentText" id="commentText" placeholder="Add a new comment"></textarea>
                    </div>
                </div>
            </div>
        );
    }
}
export default CommentForm;
